import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { 
  ChartBarIcon, 
  CurrencyDollarIcon, 
  TrendingUpIcon, 
  TrendingDownIcon,
  CalendarDaysIcon,
  TruckIcon
} from '@heroicons/react/24/outline';
import PageHeader from './PageHeader';

// Get week number utility function
const getWeekNumber = (date) => {
  const d = new Date(date.getTime());
  d.setHours(0, 0, 0, 0);
  d.setDate(d.getDate() + 3 - (d.getDay() + 6) % 7);
  const week1 = new Date(d.getFullYear(), 0, 4);
  return 1 + Math.round(((d.getTime() - week1.getTime()) / 86400000 - 3 + (week1.getDay() + 6) % 7) / 7);
};


const Reports = () => {
  const [reportType, setReportType] = useState('daily');
  const [reportData, setReportData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [filters, setFilters] = useState({
    date: new Date().toISOString().split('T')[0],
    week: getWeekNumber(new Date()),
    month: new Date().getMonth() + 1,
    year: new Date().getFullYear(),
    plateNumber: ''
  });

  const fetchReport = useCallback(async () => {
    setLoading(true);
    try {
      let url = `/api/reports/${reportType}`;
      const params = new URLSearchParams();

      switch (reportType) {
        case 'daily':
          if (filters.date) params.append('date', filters.date);
          break;
        case 'weekly':
          if (filters.week) params.append('week', filters.week);
          if (filters.year) params.append('year', filters.year);
          break;
        case 'monthly':
          if (filters.month) params.append('month', filters.month);
          if (filters.year) params.append('year', filters.year);
          break;
        case 'yearly':
          if (filters.year) params.append('year', filters.year);
          break;
        case 'car':
          if (filters.plateNumber) {
            url = `/api/reports/car/${encodeURIComponent(filters.plateNumber)}`;
          }
          break;
        default:
          break;
      }

      if (params.toString()) {
        url += `?${params.toString()}`;
      }

      const response = await axios.get(url);
      setReportData(response.data);
    } catch (error) {
      toast.error('Error fetching report');
      console.error(error);
    } finally {
      setLoading(false);
    }
  }, [reportType, filters]);

  useEffect(() => {
    fetchReport();
  }, [fetchReport]);

  const StatCard = ({ title, value, subtitle, color = 'blue', icon: Icon = CurrencyDollarIcon, trend }) => (
    <div className="bg-white overflow-hidden shadow-lg rounded-xl border border-gray-100 hover:shadow-xl transition-shadow duration-300">
      <div className="p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <div className={`w-12 h-12 bg-gradient-to-br from-${color}-400 to-${color}-600 rounded-xl flex items-center justify-center shadow-lg`}>
              <Icon className="w-6 h-6 text-white" />
            </div>
            <div className="ml-4">
              <dt className="text-sm font-medium text-gray-600 uppercase tracking-wide">{title}</dt>
              <dd className="text-2xl font-bold text-gray-900 mt-1">{value}</dd>
              {subtitle && <dd className="text-sm text-gray-500 mt-1">{subtitle}</dd>}
            </div>
          </div>
          {trend && (
            <div className={`flex items-center ${trend > 0 ? 'text-green-600' : 'text-red-600'}`}>
              {trend > 0 ? (
                <TrendingUpIcon className="w-5 h-5" />
              ) : (
                <TrendingDownIcon className="w-5 h-5" />
              )}
              <span className="text-sm font-medium ml-1">{Math.abs(trend)}%</span>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const renderDailyReport = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-3">
        <StatCard
          title="Total Income"
          value={`$${reportData?.totalIncome || 0}`}
          color="green"
        />
        <StatCard
          title="Total Expenses"
          value={`$${reportData?.totalExpenses || 0}`}
          color="red"
        />
        <StatCard
          title="Net Profit"
          value={`$${reportData?.netProfit || 0}`}
          color={reportData?.netProfit >= 0 ? 'green' : 'red'}
        />
      </div>

      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2">
        <StatCard
          title="Cars Rented"
          value={reportData?.carsRented || 0}
          color="blue"
        />
        <StatCard
          title="Repairs"
          value={reportData?.repairsCount || 0}
          color="yellow"
        />
      </div>

      {reportData?.bookings?.length > 0 && (
        <div className="bg-white shadow-lg rounded-lg p-6 border border-gray-200">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Today's Bookings</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Customer</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Car</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Amount</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {reportData.bookings.map((booking) => (
                  <tr key={booking._id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{booking.fullName}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{booking.plateNumber}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">${booking.paymentAmount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );

  const renderWeeklyReport = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-3">
        <StatCard
          title="Total Income"
          value={`$${reportData?.totalIncome || 0}`}
          subtitle={`Week ${reportData?.week}, ${reportData?.year}`}
          color="green"
        />
        <StatCard
          title="Total Expenses"
          value={`$${reportData?.totalExpenses || 0}`}
          color="red"
        />
        <StatCard
          title="Net Profit"
          value={`$${reportData?.netProfit || 0}`}
          color={reportData?.netProfit >= 0 ? 'green' : 'red'}
        />
      </div>

      {reportData?.dailyData && (
        <div className="bg-white shadow-lg rounded-lg p-6 border border-gray-200">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Daily Breakdown</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Date</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Bookings</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Income</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Repairs</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Expenses</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {reportData.dailyData.map((day) => (
                  <tr key={day.date}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{day.date}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{day.bookings}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">${day.income}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{day.repairs}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">${day.expenses}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );

  const renderCarReport = () => (
    <div className="space-y-6">
      {reportData?.car && (
        <div className="bg-white shadow-lg rounded-lg p-6 border border-gray-200">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Car Information</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div>
              <span className="text-sm text-gray-500">Car Name:</span>
              <p className="font-medium">{reportData.car.carName}</p>
            </div>
            <div>
              <span className="text-sm text-gray-500">Plate Number:</span>
              <p className="font-medium">{reportData.car.plateNumber}</p>
            </div>
            <div>
              <span className="text-sm text-gray-500">Type:</span>
              <p className="font-medium">{reportData.car.carType}</p>
            </div>
            <div>
              <span className="text-sm text-gray-500">Status:</span>
              <p className="font-medium">{reportData.car.status}</p>
            </div>
          </div>
        </div>
      )}

      {reportData?.stats && (
        <div className="grid grid-cols-1 gap-5 sm:grid-cols-4">
          <StatCard
            title="Total Income"
            value={`$${reportData.stats.totalIncome}`}
            color="green"
          />
          <StatCard
            title="Total Expenses"
            value={`$${reportData.stats.totalExpenses}`}
            color="red"
          />
          <StatCard
            title="Net Profit"
            value={`$${reportData.stats.netProfit}`}
            color={reportData.stats.netProfit >= 0 ? 'green' : 'red'}
          />
          <StatCard
            title="Utilization Rate"
            value={`${reportData.stats.utilizationRate}%`}
            color="blue"
          />
        </div>
      )}
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">Reports</h1>
          <p className="mt-2 text-sm text-gray-600">Generate and view business reports</p>
        </div>
      </div>

      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {/* Report Controls */}
        <div className="bg-white shadow-lg rounded-lg mb-6 p-6 border border-gray-200">
          <div className="grid grid-cols-1 md:grid-cols-6 gap-4 items-end">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Report Type</label>
              <select
                value={reportType}
                onChange={(e) => setReportType(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
              >
                <option value="daily">Daily Report</option>
                <option value="weekly">Weekly Report</option>
                <option value="monthly">Monthly Report</option>
                <option value="yearly">Yearly Report</option>
                <option value="car">Car Report</option>
              </select>
            </div>

            {reportType === 'daily' && (
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Date</label>
                <input
                  type="date"
                  value={filters.date}
                  onChange={(e) => setFilters({ ...filters, date: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                />
              </div>
            )}

            {reportType === 'weekly' && (
              <>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Week</label>
                  <input
                    type="number"
                    min="1"
                    max="53"
                    value={filters.week}
                    onChange={(e) => setFilters({ ...filters, week: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Year</label>
                  <input
                    type="number"
                    value={filters.year}
                    onChange={(e) => setFilters({ ...filters, year: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
              </>
            )}

            {(reportType === 'monthly' || reportType === 'yearly') && (
              <>
                {reportType === 'monthly' && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Month</label>
                    <select
                      value={filters.month}
                      onChange={(e) => setFilters({ ...filters, month: e.target.value })}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    >
                      {Array.from({ length: 12 }, (_, i) => (
                        <option key={i + 1} value={i + 1}>
                          {new Date(2000, i).toLocaleString('default', { month: 'long' })}
                        </option>
                      ))}
                    </select>
                  </div>
                )}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Year</label>
                  <input
                    type="number"
                    value={filters.year}
                    onChange={(e) => setFilters({ ...filters, year: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                  />
                </div>
              </>
            )}

            {reportType === 'car' && (
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Plate Number</label>
                <input
                  type="text"
                  placeholder="Enter plate number"
                  value={filters.plateNumber}
                  onChange={(e) => setFilters({ ...filters, plateNumber: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                />
              </div>
            )}

            <div>
              <button
                onClick={fetchReport}
                disabled={loading}
                className="w-full px-4 py-2 bg-primary-600 border border-transparent rounded-md text-sm font-medium text-white hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
              >
                {loading ? 'Generating...' : 'Generate Report'}
              </button>
            </div>
          </div>
        </div>

        {/* Report Content */}
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
          </div>
        ) : reportData ? (
          <div>
            {reportType === 'daily' && renderDailyReport()}
            {reportType === 'weekly' && renderWeeklyReport()}
            {(reportType === 'monthly' || reportType === 'yearly') && renderDailyReport()}
            {reportType === 'car' && renderCarReport()}
          </div>
        ) : (
          <div className="bg-white shadow-lg rounded-lg p-12 text-center border border-gray-200">
            <p className="text-gray-500">Select report parameters and click "Generate Report" to view data</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reports;
