import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const SplashScreen = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      navigate('/login');
    }, 3000); // Auto-redirect after 3 seconds

    return () => clearTimeout(timer);
  }, [navigate]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-600 to-primary-800 flex items-center justify-center">
      <div className="text-center">
        {/* Logo */}
        <div className="mb-8">
          <div className="w-32 h-32 mx-auto bg-white rounded-full flex items-center justify-center shadow-2xl">
            <svg className="w-16 h-16 text-primary-600" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M4 4a2 2 0 00-2 2v8a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2H4zm2 6a2 2 0 114 0 2 2 0 01-4 0zm8 0a2 2 0 114 0 2 2 0 01-4 0z" clipRule="evenodd" />
            </svg>
          </div>
        </div>

        {/* Company Name */}
        <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">
          DURDUR CAR RENTAL
        </h1>

        {/* Tagline */}
        <p className="text-xl md:text-2xl text-primary-100 mb-8">
          Your Trusted Car Rental Partner
        </p>

        {/* Loading Animation */}
        <div className="flex justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div>
        </div>

        {/* Address */}
        <div className="mt-8 text-primary-100">
          <p className="text-sm">Dhabarka dambe ee Maqaayada Fatxi, KM4, Muqdisho</p>
          <p className="text-sm">Phone: 061110093</p>
        </div>
      </div>
    </div>
  );
};

export default SplashScreen;
